import React, { useRef, useEffect, useState } from 'react';
import API from '../api';
import { Camera, AlertCircle, CheckCircle, XCircle } from 'lucide-react';

export default function LiveCamera() {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const [isStreaming, setIsStreaming] = useState(false);
    const [detections, setDetections] = useState([]);
    const [lastAlert, setLastAlert] = useState(null);
    const [stats, setStats] = useState({ person_count: 0, total_objects: 0 });
    const [yoloStatus, setYoloStatus] = useState('checking');
    const [fps, setFps] = useState(0);

    useEffect(() => {
        checkYoloService();
    }, []);

    const checkYoloService = async () => {
        try {
            const response = await API.get('/detection/yolo-health');
            setYoloStatus('online');
            console.log('‚úÖ YOLO service online:', response.data);
        } catch (error) {
            setYoloStatus('offline');
            console.error('‚ùå YOLO service offline');
        }
    };

    const startCamera = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({
                video: { width: 640, height: 480 }
            });

            if (videoRef.current) {
                videoRef.current.srcObject = stream;
                setIsStreaming(true);
                startDetection();
            }
        } catch (error) {
            console.error('Camera access error:', error);
            alert('Could not access camera. Please allow camera permissions.');
        }
    };

    const stopCamera = () => {
        if (videoRef.current && videoRef.current.srcObject) {
            videoRef.current.srcObject.getTracks().forEach(track => track.stop());
            setIsStreaming(false);
            setDetections([]);
            setStats({ person_count: 0, total_objects: 0 });
        }
    };

    const captureFrame = () => {
        const canvas = canvasRef.current;
        const video = videoRef.current;

        if (canvas && video) {
            const context = canvas.getContext('2d');
            canvas.width = video.videoWidth;
            canvas.height = video.videoHeight;
            context.drawImage(video, 0, 0, canvas.width, canvas.height);
            return canvas.toDataURL('image/jpeg', 0.8);
        }
        return null;
    };

    const startDetection = () => {
        let frameCount = 0;
        let lastTime = Date.now();

        const detectLoop = setInterval(async () => {
            if (!isStreaming) {
                clearInterval(detectLoop);
                return;
            }

            const frame = captureFrame();
            if (!frame) return;

            try {
                const response = await API.post('/detection/detect-frame', {
                    frame: frame,
                    camera_id: 'Webcam 1'
                });

                if (response.data.success) {
                    setDetections(response.data.detections || []);
                    setStats(response.data.stats || { person_count: 0, total_objects: 0 });

                    if (response.data.alerts && response.data.alerts.length > 0) {
                        setLastAlert(response.data.alerts[0]);
                    }

                    frameCount++;
                    const now = Date.now();
                    if (now - lastTime >= 1000) {
                        setFps(frameCount);
                        frameCount = 0;
                        lastTime = now;
                    }
                }
            } catch (error) {
                console.error('Detection error:', error);
                if (error.response?.status === 500) {
                    setYoloStatus('offline');
                }
            }
        }, 2000);
    };

    return (
        <div style={{ padding: '20px', background: '#1a1a1a', borderRadius: '8px', border: '1px solid #d4af37', marginBottom: '20px' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px' }}>
                <h2 style={{ color: '#d4af37', display: 'flex', alignItems: 'center', gap: '10px', margin: 0 }}>
                    <Camera size={28} /> Live Camera Feed
                </h2>
                <div style={{ display: 'flex', gap: '10px', alignItems: 'center' }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '5px' }}>
                        {yoloStatus === 'online' ? (
                            <CheckCircle size={20} color="#4caf50" />
                        ) : yoloStatus === 'offline' ? (
                            <XCircle size={20} color="#d32f2f" />
                        ) : (
                            <AlertCircle size={20} color="#ffb400" />
                        )}
                        <span style={{ color: yoloStatus === 'online' ? '#4caf50' : '#d32f2f', fontSize: '14px' }}>
                            YOLO: {yoloStatus}
                        </span>
                    </div>
                    {isStreaming ? (
                        <button onClick={stopCamera} style={{ background: '#d32f2f', color: '#fff', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold' }}>
                            ‚èπ Stop Camera
                        </button>
                    ) : (
                        <button onClick={startCamera} style={{ background: '#4caf50', color: '#fff', padding: '10px 20px', border: 'none', borderRadius: '5px', cursor: 'pointer', fontWeight: 'bold' }}>
                            ‚ñ∂ Start Camera
                        </button>
                    )}
                </div>
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '2fr 1fr', gap: '20px' }}>
                <div style={{ position: 'relative' }}>
                    <video
                        ref={videoRef}
                        autoPlay
                        playsInline
                        style={{ width: '100%', borderRadius: '8px', background: '#000', border: '2px solid #d4af37' }}
                    />
                    <canvas ref={canvasRef} style={{ display: 'none' }} />

                    {isStreaming && (
                        <div style={{ position: 'absolute', top: '10px', left: '10px', background: 'rgba(0,0,0,0.7)', padding: '5px 10px', borderRadius: '5px', color: '#4caf50', fontSize: '12px', fontWeight: 'bold' }}>
                            üî¥ LIVE | {fps} FPS
                        </div>
                    )}

                    {lastAlert && (
                        <div style={{ 
                            position: 'absolute', 
                            bottom: '10px', 
                            left: '10px', 
                            right: '10px',
                            background: lastAlert.severity === 'critical' ? 'rgba(211,47,47,0.9)' : 'rgba(255,180,0,0.9)',
                            padding: '15px',
                            borderRadius: '8px',
                            color: '#fff',
                            fontWeight: 'bold',
                            animation: 'pulse 1s infinite'
                        }}>
                            <div style={{ fontSize: '16px', marginBottom: '5px' }}>{lastAlert.message}</div>
                            <div style={{ fontSize: '12px', opacity: 0.9 }}>Confidence: {(lastAlert.confidence * 100).toFixed(0)}%</div>
                        </div>
                    )}
                </div>

                <div>
                    <div style={{ background: '#2a2a2a', padding: '15px', borderRadius: '8px', marginBottom: '15px', border: '1px solid #d4af37' }}>
                        <h3 style={{ color: '#d4af37', fontSize: '16px', marginBottom: '15px', marginTop: 0 }}>Detection Stats</h3>
                        <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
                            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                                <span style={{ color: '#ccc' }}>üë§ People:</span>
                                <span style={{ color: '#fff', fontWeight: 'bold', fontSize: '18px' }}>{stats.person_count}</span>
                            </div>
                            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                                <span style={{ color: '#ccc' }}>üì¶ Objects:</span>
                                <span style={{ color: '#fff', fontWeight: 'bold', fontSize: '18px' }}>{stats.total_objects}</span>
                            </div>
                            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                                <span style={{ color: '#ccc' }}>‚ö° FPS:</span>
                                <span style={{ color: '#4caf50', fontWeight: 'bold', fontSize: '18px' }}>{fps}</span>
                            </div>
                        </div>
                    </div>

                    <div style={{ background: '#2a2a2a', padding: '15px', borderRadius: '8px', border: '1px solid #d4af37', maxHeight: '400px', overflowY: 'auto' }}>
                        <h3 style={{ color: '#d4af37', fontSize: '16px', marginBottom: '15px', marginTop: 0 }}>Live Detections</h3>
                        {detections.length === 0 ? (
                            <p style={{ color: '#666', fontSize: '14px' }}>No objects detected</p>
                        ) : (
                            <div style={{ display: 'flex', flexDirection: 'column', gap: '8px' }}>
                                {detections.map((det, idx) => (
                                    <div key={idx} style={{ background: '#1a1a1a', padding: '10px', borderRadius: '5px', borderLeft: '3px solid #d4af37' }}>
                                        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                                            <span style={{ color: '#fff', fontSize: '14px', fontWeight: 'bold' }}>
                                                {det.class}
                                            </span>
                                            <span style={{ 
                                                background: det.confidence > 0.8 ? '#4caf50' : det.confidence > 0.6 ? '#ffb400' : '#ff6b6b',
                                                color: '#fff',
                                                padding: '2px 8px',
                                                borderRadius: '3px',
                                                fontSize: '12px'
                                            }}>
                                                {(det.confidence * 100).toFixed(0)}%
                                            </span>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>
            </div>

            <style>{`
                @keyframes pulse {
                    0%, 100% { opacity: 1; }
                    50% { opacity: 0.7; }
                }
            `}</style>
        </div>
    );
}
